class WCGoogleMap extends HTMLElement{connectedCallback(){this.config={location:{lat:this.getAttribute("lat"),lng:this.getAttribute("lng")},zoom:parseInt(this.getAttribute("zoom"))||12},document.addEventListener("DOMContentLoaded",this.initGoogleMaps.bind(this))}initGoogleMaps(){this.canvas=document.createElement("div"),this.canvas.classList.add("map-canvas"),this.appendChild(this.canvas),google.maps.event.addDomListener(window,"load",this.setupGoogleMap.bind(this))}setupGoogleMap(){let o=new google.maps.LatLng(this.config.location.lat,this.config.location.lng);var e={zoom:this.config.zoom,minZoom:6,maxZoom:17,draggable:!0,zoomControl:!0,zoomControlOptions:{style:google.maps.ZoomControlStyle.DEFAULT},center:o,mapTypeId:google.maps.MapTypeId.ROADMAP,scrollwheel:!1,panControl:!1,mapTypeControl:!1,scaleControl:!1,streetViewControl:!1,overviewMapControl:!1,rotateControl:!1};let t=new google.maps.Map(this.canvas,e);new google.maps.Marker({position:o,map:t});google.maps.event.addDomListener(window,"resize",function(){t.setCenter(o)})}}window.customElements.define("wc-google-map",WCGoogleMap);